const studentId = 24; 
const baseUrl = "http://212.237.219.35:8080"; 
const results = [];

for (let page = 0; page <= 5; page++) {
    try {
        const response = await this.helpers.httpRequest({
            method: 'GET',
            url: `${baseUrl}/students/${studentId}/cms/spares?page=${page}&size=10`,
            json: true
        });


        if (Array.isArray(response)) {
            response.forEach(item => results.push(item));
        } 

        else if (response && typeof response === 'object') {

            for (const key in response) {
                if (Array.isArray(response[key])) {
                    response[key].forEach(item => results.push(item));
                    break;
                }
            }
        }
    } catch (error) {
        console.log(`Страница ${page} ошибка: ${error.message}`);
    }
}


const output = results.map(item => ({
    json: item
}));

return output;

