const esc = s => String(s ?? '').replace(/'/g, "''");
const s = $json;

const priceNum = Number(String(s.price).replace(',', '.'));
let uaSql = String(s.updatedAt || '').trim();
if (uaSql.includes('T')) {
  uaSql = uaSql.replace('T',' ').replace('Z','');
  uaSql = uaSql.replace(/(\.\d{6})\d+$/, '$1'); 
}

const priceRaw = String(s.price ?? '');
const updatedAtRaw = String(s.updatedAt ?? '');

return {
  json: {
    ...s,
    priceNum,
    updatedAtSql: uaSql,
    priceRaw,
    updatedAtRaw,
    _e: {
      code:  esc(s.spareCode),
      name:  esc(s.spareName),
      descr: esc(s.spareDescription),
      type:  esc(s.spareType),
      status:esc(s.spareStatus),
      updatedAt: esc(uaSql)
    }
  }
};
